import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import Grid from "@mui/material/Grid";
import Box from "@mui/material/Box";
import { useState } from "react";
import GameContainer from "./GameContainer";
import AddNewGame from "./AddNewGame";
import Header from "./Header";

const Home = ({ user }) => {
  const [userGames, setUserGames] = useState([]);
  const [clickFind, setClickFind] = useState(true);
  const [addNew, setAddNew] = useState(true);

  const handleFindGames = () => {
    fetch(`http://localhost:3000/users/${user.id}/games`)
      .then((res) => res.json())
      .then((gameData) => {
        let sortedGames = gameData.sort((a,b) => (a.name > b.name) ? 1 : -1)
        setUserGames(gameData);
        setClickFind((clickFind) => !clickFind);
      });
  };

  const handleAddGame = () => {
    setAddNew((addNew) => !addNew);
  };

  return (
    <div>
      <Header />
      {clickFind && addNew ? (
        <div>
          <Box container noValidate sx={{ mt: 3 }}>
            <Grid item xs={12} sx={{ mt: 3 }}>
              <Card sx={{ maxWidth: 275 }}>
                <CardContent align="center">
                  <Typography variant="h5" component="div">
                    View Games
                  </Typography>
                </CardContent>
                <CardContent align="center">
                  <Button size="small" align="right" onClick={handleFindGames}>
                    Select games
                  </Button>
                </CardContent>
              </Card>
            </Grid>

            <Grid item xs={12} sx={{ mt: 3 }}>
              <Card sx={{ maxWidth: 275 }}>
                <CardContent align="center">
                  <Typography variant="h5" component="div">
                    Add New Game
                  </Typography>
                </CardContent>
                <CardContent align="center">
                  <Button size="small" align="right" onClick={handleAddGame}>
                    Add Game
                  </Button>
                </CardContent>
              </Card>
            </Grid>
          </Box>
        </div>
      ) : !clickFind && addNew ? (
        <div>
          <GameContainer user={user} userGames={userGames} />
        </div>
      ) : clickFind && !addNew ? (
        <div>
          <AddNewGame user={user} />
        </div>
      ) : null}
    </div>
  );
};

export default Home;
