import { useState, useEffect } from "react";
import Avatar from "@mui/material/Avatar";
import Button from "@mui/material/Button";
import CssBaseline from "@mui/material/CssBaseline";
import TextField from "@mui/material/TextField";
import Grid from "@mui/material/Grid";
import Box from "@mui/material/Box";
import LockOutlinedIcon from "@mui/icons-material/LockOutlined";
import Typography from "@mui/material/Typography";
import Container from "@mui/material/Container";
import { createTheme, ThemeProvider } from "@mui/material/styles";

const AddNewGame = ({user}) => {
const [allGames,setAllGames] = useState([])
const [gameInput,setGameInput] = useState("")
const [dateInput,setDateInput] = useState("")
const [newMatch,setNewMatch] = useState({
  game_id: null,
  date: dateInput
})
const [allPlayers,setAllPlayers] = useState([{
  user_id: user.id,
  match_id: null,
  score: null,
  is_winner: null,
  name: `${user.first_name} ${user.last_name}`
}])

const handleSubmit = (e) => {
  e.preventDefault()
  console.log(`Game Input: ${gameInput}`)
  console.log(`All Games: ${JSON.stringify(allGames,null,4)}`)
  console.log(`Date Input: ${dateInput}`)
  console.log(`All Players: ${JSON.stringify(allPlayers,null,4)}`)
  console.log(`Game For Entry: ${JSON.stringify(handleGameInput(gameInput),null,4)}`)
  console.log(`Match Input: ${JSON.stringify(newMatch,null,4)}`)
}

const handleGameInput = (input) => {
  let inputComparison = input.toLowerCase()
  let gameInDB = false
  let gameForEntry = false
  allGames.map((givenGame) => {
    let gameComparison = givenGame.name.toLowerCase()
    if (inputComparison == gameComparison) {
      gameInDB = true
      gameForEntry = givenGame
      console.log(`Game Entry Found: ${givenGame.name}`)
    }
  })
  if (gameInDB == false){
    fetch("http://localhost:3000/games", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          name: input
        }),
      }).then((res) => {
        if (res.ok) {
          res.json().then((newGameEntry) => gameForEntry = newGameEntry);
        } else {
          res.json().then((errors) => console.log(errors));
        }
      })
  }
  setNewMatch({...newMatch, game_id:gameForEntry.id, date:dateInput})
  setAllGames([...allGames].push(gameForEntry))
  return gameForEntry
}

useEffect(() => {
  fetch("http://localhost:3000/games").then((r) => {
    if (r.ok) {
      r.json().then((games) => setAllGames(games));
    }
  });
}, []);


  return (
    <div>
      <ThemeProvider theme={theme}>
        <Container component="main" maxWidth="xs">
          <CssBaseline />
          <Box
            sx={{
              marginTop: 8,
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
            }}
            onSubmit={handleSubmit}
          >
            <Typography component="h1" variant="h5">
              Add New Game
            </Typography>
            <Box component="form" noValidate sx={{ mt: 3 }}>
              <Grid container spacing={2}>
                <Grid item xs={12} sm={6}>
                  <TextField
                    required
                    fullWidth
                    autoComplete="game-name"
                    name="name"
                    id="gameName"
                    label="Game"
                    autoFocus
                    onChange={(e) => setGameInput(e.target.value)}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    required
                    fullWidth
                    autoComplete="match-date"
                    id="matchDate"
                    label="Date of Game"
                    name="date"
                    onChange={(e) => setDateInput(e.target.value)}
                  />
                </Grid>

                <Button
                  type="submit"
                  fullWidth
                  variant="contained"
                  sx={{ mt: 3, mb: 2 }}
                >
                  Add New Game
                </Button>
              </Grid>
            </Box>
          </Box>
        </Container>
      </ThemeProvider>
    </div>
  );
};

const theme = createTheme();

export default AddNewGame;
