import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import { useState } from "react";
import MatchContainer from "./MatchContainer";

const GameCard = ({ user, game }) => {
  const [matchData, setMatchData] = useState([]);
  const [selectGameOn, setSelectGameOn] = useState(true);

  const handleSelectGame = () => {
    fetch(`http://localhost:3000/users/${user.id}/games/${game.id}/matches`)
      .then((res) => res.json())
      .then((matchData) => {
        setMatchData(matchData);
        setSelectGameOn((selectGameOn) => !selectGameOn);
      });
  };

  console.log(matchData);

  return (
    <div>
      {selectGameOn ? (
        <Card sx={{ maxWidth: 275 }}>
          <CardContent align="center">
            <Typography variant="h5" component="div">
              {game.name}
            </Typography>
          </CardContent>
          <CardContent align="center">
            <Button size="small" align="right" onClick={handleSelectGame}>
              Select Game
            </Button>
          </CardContent>
        </Card>
      ) : (
        <div>
          <MatchCard />
        </div>
      )}
    </div>
  );
};

export default GameCard;
