import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import { useState } from "react";
import GameCard from "./GameCard";

const GameContainer = ({ user, game }) => {
  const [selectGameOn, setSelectGameOn] = useState(true);
  const [selectedGame, setSelectedGame] = useState("");

  const handleSelectGame = () => {
    setSelectGameOn((selectGameOn) => !selectGameOn);
  };

  return (
    <div>
      {selectGameOn ? (
        <Card sx={{ maxWidth: 275 }}>
          <CardContent align="center">
            <Typography variant="h5" component="div">
              {game.name}
            </Typography>
          </CardContent>
          <CardContent align="center">
            <Button size="small" align="right" onClick={handleSelectGame}>
              Select Game
            </Button>
          </CardContent>
        </Card>
      ) : (
        <div>
          <GameCard user={user} game={game} />
        </div>
      )}
    </div>
  );
};

export default GameContainer;
